package com.htc.corejava.exercises;

public class JavaDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean flag=true;
		char a='a';
		byte singlenumber = 1;
		short s=100;
		int i=15000;
		long l=99999999;
		double d=45.99999;
		float f=78.389f;
		
		System.out.println("Boolean Data Type Variable flag = " + flag);
		System.out.println("Char Data Type Variable a = " + a);
		System.out.println("Byte Data Type Variable singlenumber = " + singlenumber);
		System.out.println("Short Data Type Variable s = " + s);
		System.out.println("Int Data Type Variable i = " + i);
		System.out.println("Long Data Type Variable L = " + l);
		System.out.println("Double Data Type Variable d = " + d);
		System.out.println("Float Data Type Variable f = " + f);
	}

}
